@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");
@import url('https://fonts.googleapis.com/css2?family=Lexend&display=swap');

#page {
  --max-width: 740px;
  /* --color-accent: #FF0000; Beware, we look like the LPC with a red theme on*/ 
  --color-accent: rebeccapurple;
  font-family: lexend, system-ui, sans-serif;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

a {
  color: var(--color-accent);
}

.link {
  color: var(--color-accent);
}

a:hover {
  text-decoration: underline;
  font-weight: bold;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  /* vertically center */
  display: inline-flex;
  align-items: center;

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

/* Markdown styling */
.content {
  :global(img) {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  :global(p) {
    padding: 0.5rem 0;
    line-height: 1.5;
  }

  :global(a) {
    text-decoration: underline;
  }

  :global(h1) {
    font-size: 2.5rem;
    font-weight: 600;
    line-height: 1.2;
    margin-bottom: 0.5rem;
  }

  :global(h2) {
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
    line-height: 1.2;
  }

  :global(h3) {
    font-size: 1.5rem;
    font-weight: 600;
    line-height: 1.2;
    margin-top: .5rem;
  }

  :global(ul) {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  :global(ol) {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  :global(blockquote) {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

.items {
  display: grid;
  gap: 3rem;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
}

.item {
  display: grid;
  place-items: center;
  text-align: center;
  gap: 1rem;
}

.title {
  font-size: 1.25rem;
}

.link {
  font-size: 1.75rem;
}

