@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");
@import url('https://fonts.googleapis.com/css2?family=Lexend&display=swap');

#page {
  --max-width: 740px;
  font-family: lexend, system-ui, sans-serif;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  background: inherit;
    border: 2px solid var(--color-accent, rebeccapurple);
    color: var(--color, #222);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

.section-container { /* Defined in Site CSS */
  display: grid;
  justify-content: center;
}

.card {
  max-width: 640px;
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);
  padding: 3.5rem 2.75rem;
  text-align: center;
  display: grid;
  place-items: center;
  gap: 2rem;

  @media (min-width: 600px) {
    padding: 4.5rem 7rem;
  }
}

.heading {} /* Defined in Site CSS */

.link { /* Defined in Site CSS */
  font-weight: 500;
  border-color: transparent;
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: initial;
  }
}

ul {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 1.5rem;

  li {
    color: white;  
    background: var(--color-accent, rebeccapurple); 
    font-size: 1.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    transition: 0.1s transform;

    &:hover {
      transform: scale(1.1);
    }

    a {
      font-size: 1.5rem;
      padding: 0.75rem;
    }
  }
}

.section-container { /* Defined in Site CSS  */
  display: grid;
  gap: 3rem;

  @media (min-width: 700px) {
    grid-template-columns: 2fr 1fr;
    gap: 7rem;
  }
}

.content {
  display: grid;
  place-content: flex-start;
  gap: 1rem;

  .heading {} /* Defined in Site CSS  */

  .body {}
}

form {
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);
  display: grid;
  gap: 1rem;
  padding: 2rem;

  label {
    display: grid;
    gap: 0.25rem;
    font-size: 0.75rem;
  }

  input {
    width: 100%;
    border-radius: 4px;
    padding: 0.5rem 0.75rem;
    font-size: 1rem;
      transition: var(--transition, 0.1s outline-color);
      border: 1.5px solid var(--border-color, #eee);
      border-radius: 4px;

      &:focus {
        outline-color: var(--color-accent, rebeccapurple);
      }
  }

  .button {
    width: 100%;
  }
}

