      &:hover {
        border-color: initial;
      }
    }
  }

  @media (min-width: 800px) {
    display: flex;
  }
}

.mobile-nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  font-weight: 500;

  @media (min-width: 800px) {
    display: none;
  }
}

